mapper.select:
SELECT 受注.受注ID AS "salesOrderSummary.salesOrderId.value", 受注番号 AS "salesOrderSummary.salesOrderNumber.value", 受注.顧客ID AS "salesOrderSummary.customer.code.value", 顧客姓 AS "salesOrderSummary.customer.name.lastName", 顧客名 AS "salesOrderSummary.customer.name.firstName", 顧客姓カナ AS "salesOrderSummary.customer.nameKana.lastName", 顧客名カナ AS "salesOrderSummary.customer.nameKana.firstName", 受注日 AS "salesOrderSummary.orderedDate.value", '引当済' AS "allocatedStatus" FROM 受注.受注 INNER JOIN 顧客.顧客 ON 受注.顧客ID = 顧客.顧客ID INNER JOIN 引当._引当完了受注 ON 受注.受注ID = 引当完了受注.受注ID
#{allocationCriteria.from.value} <= 受注日
AND 受注日 <= #{allocationCriteria.to.value}

SELECT 受注.受注ID AS "salesOrderSummary.salesOrderId.value", 受注番号 AS "salesOrderSummary.salesOrderNumber.value", 受注.顧客ID AS "salesOrderSummary.customer.code.value", 顧客姓 AS "salesOrderSummary.customer.name.lastName", 顧客名 AS "salesOrderSummary.customer.name.firstName", 顧客姓カナ AS "salesOrderSummary.customer.nameKana.lastName", 顧客名カナ AS "salesOrderSummary.customer.nameKana.firstName", 受注日 AS "salesOrderSummary.orderedDate.value", '引当残あり' AS "allocatedStatus" FROM 受注.受注 INNER JOIN 顧客.顧客 ON 受注.顧客ID = 顧客.顧客ID
#{salesOrderId.value}
AND #{allocationCriteria.from.value} <= 受注日
AND 受注日 <= #{allocationCriteria.to.value}
