mapper.insert:
INSERT INTO 出荷指示.出荷指示 (出荷番号, 受注ID, 出荷日)
VALUES (
        #{shippingNumber},
        #{shipping.salesOrderId.value},
        #{shipping.shippingDate.value}
       )

INSERT INTO 出荷指示.出荷指示済引当 (出荷番号, 引当番号)
VALUES (
        #{shippingNumber},
        #{singleAllocation.singleAllocationNumber.value}
        )

INSERT INTO 出荷指示.出荷指示済セット商品引当 (出荷番号, 引当番号)
VALUES (
        #{shippingNumber},
        #{bundleAllocation.bundleAllocationNumber.value}
        )

mapper.select:
SELECT 出荷指示.出荷番号 AS "shippingNumber.value", 出荷日 AS "shippingInstructionContent.shippingDate.value", 受注.受注ID AS "shippingInstructionContent.salesOrderNumber.value", 受注日 AS "orderedDate.value", 顧客.顧客ID AS "customer.code.value", 顧客姓 AS "customer.name.lastName", 顧客名 AS "customer.name.firstName", 顧客姓カナ AS "customer.nameKana.lastName", 顧客名カナ AS "customer.nameKana.firstName" FROM 出荷指示.出荷指示 INNER JOIN 受注.受注 ON 出荷指示.受注ID = 受注.受注ID INNER JOIN 顧客.顧客 ON 受注.顧客ID = 顧客.顧客ID INNER JOIN 出荷指示._未出荷 ON 出荷指示.出荷番号 = 出荷指示._未出荷.出荷番号
#{shippingInstructionCriteria.from.value} <= 受注日
AND 受注日 <= #{shippingInstructionCriteria.to.value}
